CREATE TRIGGER trigger_price_history
AFTER UPDATE ON courses
FOR EACH ROW
BEGIN
    IF OLD.price != NEW.price THEN
        VALUES (NEW.id, OLD.price, NEW.price);
    END IF;
END //

DELIMITER ;






